    const data = users.map(user =>{
        const  { phoneNumbers } = user;
        const hasEmptyPhoneNumber = Object.keys(phoneNumbers[0]).length===0;
    
    return {
        ...user,
        phoneNumbers: hasEmptyPhoneNumber? []:phoneNumbers
    }
});
return{
    data,
    message:'list of all users'
}
};


